import pandas as pd # type: ignore

# Load the dataset (Make sure this path is correct)
try:
    df = pd.read_csv('Iris.csv')
    print("Dataset loaded successfully!")
except FileNotFoundError:
    print("File not found. Please check the file path.")

# Check the column names and inspect the first few rows of the dataset
print("Column names in the dataset:", df.columns)
print("\nFirst few rows of the dataset:\n", df.head())

# Clean the dataset
# Strip spaces from column names to handle potential formatting issues
df.columns = df.columns.str.strip()

# Check again after cleaning column names
print("\nColumn names after cleaning:", df.columns)

# Handle missing values in 'species' or equivalent categorical column if it exists
if 'species' in df.columns:
    df['species'] = df['species'].fillna(df['species'].mode()[0])
else:
    print("No column named 'species' found in the dataset.")

# Handle missing values in numeric columns by filling them with the mean
numeric_columns = df.select_dtypes(include=['float64', 'int64']).columns
df[numeric_columns] = df[numeric_columns].fillna(df[numeric_columns].mean())

